import spark.implicits._


case class CleanRecord (date:java.sql.Timestamp, dailyConfirmed:Int, dailyDischarged:Int, dischargedToIsolation:Int, dailyDeaths:Int, testedPositiveDemise:Int, dailyImported:Int, dailyLocalTransimission:Int, localCasesResidingInDorms:Int, localCasesNotResidinginDorms:Int)


def noneIs0(o:Option[Int]) : Int = o match { 
    case Some(v) => v
    case None => 0
}

def clean(r:Record): CleanRecord = r match {
    case Record(date, dailyConfirmed, dailyDischarged, dischargedToIsolation, dailyDeaths, testedPositiveDemise, dailyImported, dailyLocalTransimission, localCasesResidingInDorms, localCasesNotResidinginDorms) => {
        CleanRecord(date, dailyConfirmed, dailyDischarged, dischargedToIsolation, dailyDeaths, testedPositiveDemise, dailyImported, dailyLocalTransimission, noneIs0(localCasesResidingInDorms), noneIs0(localCasesNotResidinginDorms))
    }
}

records.map( clean ).show()
